import React, { Component } from 'react';
import { outfits } from './Outfits'; // Make sure the path to your data is correct

export default class Carousel extends Component {
  render() {
    const rows = [];
    for (let i = 0; i < outfits.length; i += 3) {
      const rowOutfits = outfits.slice(i, i + 3);

      rows.push(
        <div className="row" key={`row-${i}`}>
          {rowOutfits.map((outfit, index) => (
            <div className="card" style={{width:"14em", backgroundColor:"#F8E831"}}key={`outfit-${i + index}`}>
              <img style={{width:"13em",height:"17em",display:"block"}}src={outfit.image} alt="clothes" />
              <button
                    style={{ marginTop: "-2em",backgroundColor:"#047BD5"}}
                    type="button"
                    className="btn btn-warning"

                    onClick={() => (window.location.href = outfit.link)}
                    >
                    Try this Out!
            </button>

            <button type="button" class="btn btn-outline-primary">Primary</button>
            </div>
          ))}
        </div>
      );
    }

    return <div className="container carousel">{rows}</div>;
  }
}
