import React, { Component } from 'react';

class AutoTypingText extends Component {
  state = {
    text: '',
    currentIndex: 0,
  };

  componentDidMount() {
    const { content } = this.props;

    this.typingInterval = setInterval(this.typeCharacter, 100); // Adjust the delay here
    this.setState({ text: content });
  }

  componentWillUnmount() {
    clearInterval(this.typingInterval);
  }

  typeCharacter = () => {
    const { content } = this.props;
    const { text, currentIndex } = this.state;

    if (currentIndex < content.length) {
      this.setState({ text: text + content[currentIndex], currentIndex: currentIndex + 1 });
    } else {
      clearInterval(this.typingInterval);
    }
  };

  render() {
    const { text } = this.state;

    return (
      <div className="auto-typing-text">
        {text}
      </div>
    );
  }
}

export default AutoTypingText;
