import React, { Component } from 'react';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from "@chatscope/chat-ui-kit-react";
import axios from "axios";

const API_KEY = "1f38974303mshd6e14d884ce909fp1a50f6jsnb816a2be044a"; // Update with your API key

class FashionAssistant extends Component {
  constructor(props) {
    super(props);
    this.state = {
      typing: false,
      messages: [
        {
          message: "Hello, I am ChatGPT",
          sender: "ChatGPT"
        }
      ]
    };
  }

  handleSend = async (message) => {
    const { messages } = this.state;

    const newMessage = {
      message: message,
      sender: "user",
      direction: "outgoing"
    };

    const newMessages = [...messages, newMessage];
    this.setState({ messages: newMessages, typing: true });

    await this.processMessageToChatGPT(message);
  }

  processMessageToChatGPT = async (userMessage) => {
    const { messages } = this.state;

    const newUserMessage = {
      message: userMessage,
      sender: "user",
      direction: "outgoing"
    };

    const newMessages = [...messages, newUserMessage]; // Add the user's message
    this.setState({ messages: newMessages, typing: true });

    const options = {
      method: 'POST',
      url: 'https://open-ai21.p.rapidapi.com/conversationllama',
      headers: {
        'content-type': 'application/json',
        'X-RapidAPI-Key': API_KEY,
        'X-RapidAPI-Host': 'open-ai21.p.rapidapi.com'
      },
      data: {
        messages: [
          {
            role: 'user',
            content: userMessage // Use the user's message here
          }
        ],
        web_access: false
      }
    };

    try {
      const response = await axios.request(options);
     console.log(response.data);
      const assistantMessage=response.data.LLAMA;
      const newAssistantMessage={
        message:assistantMessage,
        sender: "ChatGPT",
        direction: "incoming"
      }
      const updatedMessages = [...newMessages, newAssistantMessage];
      this.setState({ messages: updatedMessages, typing: false });
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    const { typing, messages } = this.state;

    return (
      <div className='FashionAssistant' style={{backgroundColor:"#EDEADE"}}>
        <a href="#" class="card">
  <img src="https://images.unsplash.com/photo-1526297003708-f5a1c2c9c6e7?crop=entropy&cs=tinysrgb&fm=jpg&ixid=MnwzMjM4NDZ8MHwxfHJhbmRvbXx8fHx8fHx8fDE2NjI0OTY1ODM&ixlib=rb-1.2.1&q=80" alt="balloon with an emoji face" class="card__img"/>
  <span class="card__footer">
    <span>Awesome speedy card</span>
    <span>2 minutes!</span>
  </span>
  <span class="card__action">
    <svg viewBox="0 0 448 512" title="play">
      <path d="M424.4 214.7L72.4 6.6C43.8-10.3 0 6.1 0 47.9V464c0 37.5 40.7 60.1 72.4 41.3l352-208c31.4-18.5 31.5-64.1 0-82.6z" />
    </svg>
  </span>
</a>
        <div style={{ position: "relative", height: "700px", width: "800px" }}>

          <MainContainer className='MainContainer' >
            <div className='Header' style={{width:"250px"}}>Fashion Assistant Chat</div>

            <ChatContainer className='ChatContainer' style={{width:"40em"}}>
              <MessageList
                scrollBehavior='smooth'
                typingIndicator={typing ? <TypingIndicator content="ChatGPT is Typing" /> : null}
                className='MessageList'
              >
                {messages.map((message, i) => (   
                  <Message
                    key={i}
                    model={message}
                    className={message.sender === "ChatGPT" ? 'AssistantMessage' : 'UserMessage'}
                  />
                ))}
              </MessageList>
              <MessageInput placeholder='Type Message Here' onSend={this.handleSend} className='MessageInput' />
            </ChatContainer>
          </MainContainer>
        </div>
        <div>
          <h1>See what's Trending on Instagram</h1>
          <div className="pic-ctn" style={{width:"40em",marginRight:"0em", marginTop:"0em"}}>
            <img src="https://picsum.photos/200/300?t=1" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=2" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=3" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=4" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=5" alt="" className="pic"/>
          </div>
        </div>
      </div>
    );
  }
}

export default FashionAssistant;
