import React, { Component } from 'react';

class AutoTypingTextAnimation extends Component {
  state = {
    text: '',
    currentCharIndex: 0,
    typingInterval: null,
  };

  componentDidMount() {
    const { content } = this.props;
    this.startTyping(content);
  }

  componentWillUnmount() {
    this.clearTypingInterval();
  }

  startTyping = (content) => {
    this.clearTypingInterval();
    const typingInterval = setInterval(this.typeCharacter, 100); // Adjust the delay here
    this.setState({
      text: '',
      currentCharIndex: 0,
      typingInterval,
    });
  };

  clearTypingInterval = () => {
    clearInterval(this.state.typingInterval);
  };

  typeCharacter = () => {
    const { content } = this.props;
    const { text, currentCharIndex } = this.state;

    if (currentCharIndex < content.length) {
      this.setState({
        text: text + content[currentCharIndex],
        currentCharIndex: currentCharIndex + 1,
      });
    } else {
      this.clearTypingInterval();
    }
  };

  render() {
    const { text } = this.state;

    return (
      <div className="auto-typing-text-animation">
        {text}
      </div>
    );
  }
}

export default AutoTypingTextAnimation;
