import React, { Component } from 'react';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from "@chatscope/chat-ui-kit-react";
import axios from "axios";


const API_KEY = "1f38974303mshd6e14d884ce909fp1a50f6jsnb816a2be044a"; // Update with your API key

class FashionAssistant extends Component {
  constructor(props) {
    super(props);
    this.state = {
      typing: false,
      messages: [
        {
          message: "Hello, I am ChatGPT",
          sender: "ChatGPT"
        }
      ]
    };
  }

  handleSend = async (message) => {
    const { messages } = this.state;

    const newMessage = {
      message: message,
      sender: "user",
      direction: "outgoing"
    };

    const newMessages = [...messages, newMessage];
    this.setState({ messages: newMessages, typing: true });

    await this.processMessageToChatGPT(message);
  }

  processMessageToChatGPT = async (userMessage) => {
    const { messages } = this.state;

    const newUserMessage = {
      message: userMessage,
      sender: "user",
      direction: "outgoing"
    };

    const newMessages = [...messages, newUserMessage]; // Add the user's message
    this.setState({ messages: newMessages, typing: true });

    const options = {
      method: 'POST',
      url: 'https://open-ai21.p.rapidapi.com/conversationllama',
      headers: {
        'content-type': 'application/json',
        'X-RapidAPI-Key': API_KEY,
        'X-RapidAPI-Host': 'open-ai21.p.rapidapi.com'
      },
      data: {
        messages: [
          {
            role: 'user',
            content: userMessage // Use the user's message here
          }
        ],
        web_access: false
      }
    };

    try {
      const response = await axios.request(options);
     console.log(response.data);
      const assistantMessage=response.data.LLAMA;
      const newAssistantMessage={
        message:assistantMessage,
        sender: "ChatGPT",
        direction: "incoming"
      }
      const updatedMessages = [...newMessages, newAssistantMessage];
      this.setState({ messages: updatedMessages, typing: false });
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    const { typing, messages } = this.state;

    return (
      <div className='FashionAssistant' style={{marginRight:"0em",width:"80em"}}>

        <div style={{marginTop:"3em", ma}} >
        <button type="button" class="btn btn-primary" style={{marginTop:"0"}}>Explore on FlipKart</button>
          <div className="card" style={{width:"14em",height:"5em",marginBottom:"3.5em" ,marginRight:"1em"}}>
          <a href='https://www.flipkart.com/hidesign-green-sling-bag-eda-02/p/itmec27ea3e3b479?pid=SLBGKY8F5GSX6CPF&lid=LSTSLBGKY8F5GSX6CPFXZIBHZ&marketplace=FLIPKART&store=reh%2Fihu%2Fmf2&srno=b_1_6&otracker=browse&fm=organic&iid=0b788956-6e3f-435d-b1c6-1ff6780af25c.SLBGKY8F5GSX6CPF.SEARCH&ppt=browse&ppn=browse&ssid=ndjliu6z7k0000001692188536421'>
              <img style={{height:"7em",width:"13.8em",marginTop:"2em"}}src="https://rukminim2.flixcart.com/image/832/832/xif0q/sling-bag/i/l/n/eda-02-1-6-5-eda-02-sling-bag-hidesign-16-5-original-imagky8fszhz5zqx.jpeg?q=70"/></a>
            </div>
            <div className="card" style={{width:"14em",height:"5em",marginBottom:"3.5em",marginRight:"1em"}}>
            <a href='https://www.flipkart.com/ray-ban-round-sunglasses/p/itmcb418d2e12bd5?pid=SGLFUTGSFKFH6HXB&lid=LSTSGLFUTGSFKFH6HXBNUQKNO&marketplace=FLIPKART&store=26x&srno=b_2_57&otracker=nmenu_sub_Men_0_Sunglasses&fm=organic&iid=60ecc346-c1f3-465c-93f8-42f2390e4352.SGLFUTGSFKFH6HXB.SEARCH&ppt=browse&ppn=browse'>
              <img style={{height:"5em",width:"13.8em",marginTop:"2em"}}src="https://rukminim2.flixcart.com/image/832/832/kekadu80-0/sunglass/g/j/o/0rb3548n91233m51-51-ray-ban-original-imafv7azuncth4pk.jpeg?q=70"/></a>
            </div>
            <div className="card" style={{width:"14em",height:"11.5em",marginBottom:"1em",marginRight:"1em"}}>
            <a href='https://www.flipkart.com/titan-nq1805sm03-neo-gents-v-analog-watch-men/p/itme5a15c1b42323?pid=WATFVHZ5HDFENKS8&lid=LSTWATFVHZ5HDFENKS877BXNW&marketplace=FLIPKART&q=watches&store=r18&srno=s_1_2&otracker=search&otracker1=search&fm=Search&iid=en_6jsDP8GbrDu-4NN-awI5zMzfHj3TnGjlAIXm2y0MV34j3_v_tWN-v9H-SWVJg9Mw3IFaVQ8PzZBSxQRVh1MUFQ%3D%3D&ppt=sp&ppn=sp&ssid=cbmdx0m90g0000001692189004168&qH=2d6b6aad43dffa91'>
              <img style={{height:"11.4em",width:"13.7em"}}src="https://rukminim2.flixcart.com/image/832/832/xif0q/watch/p/l/s/-original-imagqfu2zskctnfg.jpeg?q=70"/></a>
            </div>
        </div>
        <div style={{ position: "relative", height: "700px", width: "800px" }}>

          <MainContainer className='MainContainer' >

            <ChatContainer className='ChatContainer' style={{width:"37em"}}>
              <MessageList
                scrollBehavior='smooth'
                typingIndicator={typing ? <TypingIndicator content="ChatGPT is Typing" /> : null}
                className='MessageList'
              >
                {messages.map((message, i) => (   
                  <Message
                    key={i}
                    model={message}
                    className={message.sender === "ChatGPT" ? 'AssistantMessage' : 'UserMessage'}
                  />
                ))}
              </MessageList>
              <MessageInput placeholder='Type Message Here' onSend={this.handleSend} className='MessageInput' />
            </ChatContainer>
          </MainContainer>
        </div>
        <div className='Trending' style={{width:"1000em"}}>
          <h1>See what's Trending on Instagram</h1>
          <div className="pic-ctn" style={{width:"40em",marginRight:"-6em", marginTop:"0em"}}>
            <img src="https://picsum.photos/200/300?t=1" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=2" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=3" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=4" alt="" className="pic"/>
            <img src="https://picsum.photos/200/300?t=5" alt="" className="pic"/>
          </div>
        </div>
      </div>
    );
  }
}

export default FashionAssistant;
